import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { User } from '../models';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {


  user!: User
  form!: FormGroup
  param$!: Subscription
  userId!: string
  username!: string



  constructor(private fb: FormBuilder, private router: Router, private ar: ActivatedRoute, private loginSvc : Login){}

  ngOnInit(): void {
    this.loginSvc.createUser(formData).then(result => {
      this.id = result["id"]
      this.username = result["username"]

        this.router.navigate([`/home/${this.id}`])
      }).catch(error => {
      console.log(error)
    })
    this.form = this.createForm();
  }

  search(){
    const keyword: string = this.form.value['keyword']
    console.log("search for", keyword)

    this.router.navigate([`/books/${keyword}`])
  }

  createForm(): FormGroup {
    return this.fb.group({
      keyword: this.fb.control<string>("", [Validators.required])
    })

  }

}
