import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { LoginService } from '../services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {


  loginForm!: FormGroup
  values$!: Subscription
  state$!: Subscription
  id!: string 
  username!: string
  password!: string
  constructor(private fb: FormBuilder, private router: Router,private loginSvc: LoginService) { }



  ngOnInit(): void {
    this.loginForm = this.createForm()  }

  createUser(){

    const formData = new FormData()
    formData.set("username", this.loginForm.value['username'])
    formData.set("password", this.loginForm.value['password']) 
    formData.set("email", this.loginForm.value['email'])


    this.loginSvc.createUser(formData).then(result => {
      this.id = result["userId"]
      this.username = result["username"]
      localStorage.setItem("userId", this.id)
        this.router.navigate([`/home/${this.id}`])
      }).catch(error => {
      console.log(error)
    })

      this.loginForm = this.createForm()
  }



  private createForm(): FormGroup {
    return this.fb.group({
      username: this.fb.control('', [ Validators.required ]),
      password: this.fb.control('', [ Validators.required ]),
      email: this.fb.control('', [Validators.required])
    })
  }






}
<h2>Login</h2>

<form [formGroup]="loginForm" (ngSubmit)="createUser()">
  <table>
    <tr>
      <td>Username:</td>
      <td>
        <input type="text" size="30" placeholder="Username" formControlName="username">
      </td>
    </tr>
    <tr>
        <td>Email:</td>
        <td>
          <input type="text" size="30" placeholder="Email" formControlName="email">
        </td>
      </tr>
    <tr>
      <td>Password:</td>
      <td>
        <input type="text" size="30" placeholder="Password" formControlName="password">
      </td>
    </tr>
    <tr>
      <td></td>
      <td>
        <button type="submit">Login</button>
      </td>
    </tr>
  </table>

</form>
